window.onload = function(){
	var html = document.getElementsByTagName("html")[0];
	  var oWidth = document.body.clientWidth || document.documentElement.clientWidth;
	  html.style.fontSize = oWidth*0.1 + "px";
  window.onresize = function(){
	  var html = document.getElementsByTagName("html")[0];
	  var oWidth = document.body.clientWidth || document.documentElement.clientWidth;
	  html.style.fontSize = oWidth*0.1 + "px";
  };
  };
  